import React from 'react';
import GeoLocation from '../../helper/helperComponents/GeoLocation';

class LastPage extends React.Component {

   constructor(props) {
      super(props);
      this.state = true;
      this.allowButton = this.allowButton.bind(this)
      this.helpSent = this.helpSent.bind(this)
   }

   allowButton() {
      this.setState({
         condition:!this.state
      });
   };

   helpSent() {
      alert("Help will be sent!")
   }

   render() {



      return (
         <div>
            <h1>Let us get your location.</h1>
            <GeoLocation onClick={this.allowButton}/>
            <button disabled={this.state} className="FirstPageButton" onClick={this.helpSent}>Submit</button>
         </div>
      );
   }
}

export default LastPage;
